import { Component } from '@angular/core';
import { BASE_ENDPOINT } from 'src/app/config/app';
import { Producto } from 'src/app/models/producto';
import { ProductoService } from 'src/app/services/producto.service';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-productos',
  templateUrl: './productos.component.html',
  styleUrls: ['./productos.component.css']
})
export class ProductosComponent {

  titulo = 'Listado de productos';
  productos: Producto[];

  baseEndpoint = BASE_ENDPOINT

  constructor(private service: ProductoService) { }

  ngOnInit(): void {
    this.service.listar().subscribe(productos => this.productos = productos)

  }

  generarRandomValue(): number {
    return Math.random();
  }

  public eliminar(producto: Producto): void {

    Swal.fire({
      title: 'Cuidado: ',
      text: `¿Seguro que deseas eliminar a ${producto.nombre}?`,
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
      confirmButtonText: 'Si, Eliminar!'
    }).then((result) => {
      if (result.value) {
        this.service.eliminar(producto.id).subscribe(() => {
          // al usar filter nos crea una nueva lista sin el producto eliminado, se iguala a la lista producto
          this.productos = this.productos.filter(p => p !== producto);
          Swal.fire('Eliminado', `Producto  ${producto.nombre} eliminado con exito`, 'success');
        });

      }
    })
  }
}
<h1>{{titulo}}</h1>
<button class="btn btn-success my-2" routerLink="/producto/crear">Ingresar</button>
<div class="alert alert-info" *ngIf="productos?.length == 0">
    No hay Productos en el sistema.
</div>
<!-- el signo ? en la validacion de formulario, valida que el objeto si es indefinido a null y 
    depende no se invoca le atributo.-->
<table class="table table-hover table-striped" *ngIf="productos?.length > 0">
    <thead>
        <tr>
            <th>id</th>
            <th>Foto</th>
            <th>Nombre</th>
            <th>Marca</th>
            <th>Precio</th>
            <th>Creado en</th>
            <th>Editar</th>
            <th>Eliminar</th>
        </tr>
    </thead>
    <tbody>
        <!--Angular incluye una directiva para poder iterar -->
        <tr *ngFor="let producto of productos">
            <td>{{producto.id}}</td>
            <td>
                <!--En el src se colocan entre corchetes para indicar que vamos a poblar, 
                    inyecto o hace run binding del atributo src de la imagen, que seria la forma de angular-->
                <img *ngIf="producto.fotoHashCode" 
                [src]="baseEndpoint + '/uploads/img/' + producto.id + '?fotoHashCode=' + producto.fotoHashCode + '&nocache=' + randomValue" class="img-thumbnail rounded" 
                style="width: 64px; cursor: pointer;" alt="">

                <img *ngIf="!producto.fotoHashCode"
                [src]="'/assets/img/NoFoto.png'" class="img-thumbnail rounded" 
                style="width: 64px; cursor: pointer;" alt="">

            </td>
            <td>{{producto.nombre}}</td>
            <td>{{producto.marca}}</td>
            <td>{{producto.precio}}</td>
            <td>{{producto.createAt}}</td>
            <!--El router link va en corcheter por que se va pasar una variable, no una expresion como un string.
            ya que va contenr la ruta con el ID-->
            <td><button class="btn btn-secondary btn-sm" [routerLink]="['/producto/editar/', producto.id]">Editar</button></td>
            <!--En el boton eliminar tiene que haber un evento click que invoque un metodo que se encargue de eliminar-->
            <td><button class="btn btn-danger btn-sm" (click)="eliminar(producto)">Eliminar</button></td>
        </tr>
    </tbody>
</table>

<div class="container text-center">
    <div class="row row-cols-2 row-cols-lg-5 g-2 g-lg-3">
        <div class="col" *ngFor="let producto of productos">
            <div class="p-3">
                {{producto.id}}<br>
                {{producto.nombre}}<br>
                {{producto.marca}}<br>
                {{producto.precio}}<br>
                {{producto.createAt}}
            </div>
        </div>
    </div>
</div>
